script:
frappe.ui.form.on('Vehicle', {
	refresh(frm) {
		if (!frm.is_new()){

			frm.add_custom_button(
				__("Update Odometer"),
				function () {
					frm.events.update_odometer(frm);
				},
				__("Manage"),
			);
		}
	},
	update_odometer(frm){
	    frappe.prompt({
                label: 'Odometer Reading',
                fieldname: 'odometer',
                fieldtype: 'Int'
            }, (values) => {
                if (values.odometer && Number(values.odometer) && values.odometer > 0){
    	            frappe.call({
                        method: "malaria_partners.transport.transport_utils.updated_odometer_value",
                        args: {
                            vehical: frm.doc.license_plate,
                            new_odometer_value: values.odometer
                        },
                        callback: function(r){
                            if (!r.error){
                                frm.reload_doc();

                                // frm.refresh_field('last_odometer');
                            }
                        }
                    });
                }
                else {
                    frappe.show_alert({
                        message: __("Invalid odometer reading"),
                        indicator: 'red'
                    }, 4);
                }
        });
	}
});
